// Modal.js
import React from 'react';

const Modal = ({ isOpen, onClose, event, allEvents }) => {
  // Check if event is defined
  if (!event) {
    return null; // Return null if event is undefined
  }

  // Extract properties from event with default values
  const { title = '', start = '', time = '' } = event;

  // Format start date if it's defined
  const startDateString = start ? new Date(start).toLocaleDateString() : 'N/A';

  return (
    <div className={`modal fade ${isOpen ? 'show' : ''}`} tabIndex="-1" role="dialog" style={{ display: isOpen ? 'block' : 'none' }}>
      <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">{title}</h5>
            <button type="button" className="close" onClick={onClose}>
              <span>&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <p>Date: {startDateString}</p>
            <p>Time: {time}</p>
            {/* Add more event details as needed */}
            <h5>All Events:</h5>
            <table className="table">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Date</th>
                  <th>Time</th>
                </tr>
              </thead>
              <tbody>
                {allEvents.map((event, index) => (
                  <tr key={index}>
                    <td>{event.title}</td>
                    <td>{event.start}</td>
                    <td>{event.time}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
