import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
import './styles.css';


const localizer = momentLocalizer(moment);

const App = () => {

  const views = ['month', 'agenda']; // Only include the 'month' view


    const [events, setEvents] = useState([]);
    const [selectedEvent, setSelectedEvent] = useState(null);

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const response = await axios.get('http://localhost:5000/events');
            setEvents(response.data);
        } catch (error) {
            console.error('Error fetching events:', error);
        }
    };

    const handleEventClick = event => {
        setSelectedEvent(event);
    };

    const closeModal = () => {
        setSelectedEvent(null);
    };

    return (
        <div className="app">
          <div className='container-fluid'>
            <h1>Calendar</h1>
            <Calendar
            className="calendar"
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                style={{ height: 630 }}
                views={views}
                onSelectEvent={handleEventClick}
            />
            <Modal  size="lg"  show={!!selectedEvent} onHide={closeModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Event Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                {selectedEvent && (
                      <div>
                          <h2>{selectedEvent.title}</h2>
                          <Table striped="columns">
      <thead>
        <tr>
        
          <th>raghu_start time:</th>
          <th>raghu_end time:</th>
          <th>Username</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
        <tr>
        
          <td>{selectedEvent.rahukalam_starttime}</td>
          <td>{selectedEvent.rahukalam_endtime}</td>
          <td>@mdo</td>
          <td>@mdo</td>
        </tr>
        <tr>
         
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
        </tr>
      
      </tbody>
    </Table>
                          <table className="table">
                              <tbody>
                                 
                                  <tr>
                                      <td>Location:</td>
                                      <td>{selectedEvent.location}</td>
                                  </tr>
                                  <tr>
                                      <td>raghu_start time:</td>
                                      <td>{selectedEvent.raghu_starttime}</td>
                                  </tr>
                                  <tr>
                                      <td>raghu_end time:</td>
                                      <td>{selectedEvent.raghu_endtime}</td>
                                  </tr>
                                  <tr>
                                      <td>yamakandam_start time:</td>
                                      <td>{selectedEvent.yamakandam_starttime}</td>
                                  </tr>
                                  <tr>
                                      <td>yamakandam_end time:</td>
                                      <td>{selectedEvent.yamakandam_endtime}</td>
                                  </tr>
                                  <tr>
                                      <td>yamakandam_end time:</td>
                                      <td>{selectedEvent.yamakandam_endtime}</td>
                                  </tr>
                                  
                                  <tr>
                                      <td>Start:</td>
                                      <td>{selectedEvent.start}</td>
                                  </tr>
                                  <tr>
                                      <td>End:</td>
                                      <td>{selectedEvent.end}</td>
                                  </tr>
                              </tbody>
                          </table>
                      </div>
                  )}

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
            </div>
        </div>
    );
};

export default App;
