import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
import './styles.css';


const localizer = momentLocalizer(moment);

const App = () => {

  const views = ['month', 'agenda']; // Only include the 'month' view


    const [events, setEvents] = useState([]);
    // const [selectedEvent, setSelectedEvent] = useState(null);
    const [selectedDate, setSelectedDate] = useState(null);

    useEffect(() => {
        fetchEvents();
    }, []);

    const fetchEvents = async () => {
        try {
            const response = await axios.get('http://localhost:5000/events');
            setEvents(response.data);
        } catch (error) {
            console.error('Error fetching events:', error);
        }
    };

    const handleEventClick = event => {
        setSelectedEvent(event);
    };

    const closeModal = () => {
        setSelectedEvent(null);
    };

    return (
        <div className="app">
          <div className='container'>
            <h1>Calendar!!!!!!</h1>
            <Calendar
            className="calendar"
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                style={{ height: 630 }}
                views={views}
                // onSelectEvent={handleEventClick}
                onSelectEvent={handleDateClick} // Change to onSelectEvent
            />
            <Modal   size="lg"  show={!!selectedEvent} onHide={closeModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Event Details</Modal.Title>
                </Modal.Header>
                <Modal.Body className='modal_box'>
                {selectedDate && (
                      <div className='container'>
                          <h2>{selectedEvent.title}</h2>

                          <Table responsive striped>
                        <thead>
                            <tr>
                            <th>Tamil Year</th>
                            <th>Tamil Month</th>
                            <th>Tamil Ritu</th>
                            </tr>
                        </thead>
                        <tbody  style={{alignItems:'center'}}>
                            <tr >
                            <td>{selectedEvent.tamil_year}</td>
                            <td>{selectedEvent.tamil_month}</td>
                            <td>{selectedEvent.tamil_ritu}</td>
                            </tr>
                        </tbody>
                        </Table>

                          {/* <Table responsive striped>
                        <thead>
                            <tr>
                            <th>Nakshatram</th>
                            <th>Nakshatram time</th>
                            <th>Yogam</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr >
                            
                            <td>{selectedEvent.nakshatra}</td>
                            <td>{selectedEvent.nakshatra_starttime}</td>
                            <td>{selectedEvent.yogam}</td>
                            </tr>
                        </tbody>
                        </Table>

                          <Table responsive striped>
                        <thead>
                            <tr>
