import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';

function App() {
  const [events, setEvents] = useState([]);

  const handleDateClick = async (arg) => {
    const date = arg.dateStr;
    try {
      const response = await fetch(`http://localhost:5001/events/${date}`);
      const data = await response.json();
      console.log('Events for clicked day:', data);
      // Implement logic to open modal box and display events for the clicked day
    } catch (error) {
      console.error('Error fetching events for clicked day:', error);
    }
  };
  

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch('http://localhost:5001/events');
      const data = await response.json();
      setEvents(data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  return (
    <div className="App">
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        dateClick={handleDateClick}
      />
    </div>
  );
}

export default App;
