// Modal.js
import React from 'react';
import Table from 'react-bootstrap/Table';

const Modal = ({ isOpen, onClose, event }) => {
  // Null check for event
  if (!event) {
    return null; // Return null if event is null
  }

  // Null check for start and end properties
  const start = event.start ? event.start.toLocaleString() : 'N/A';
  const end = event.end ? event.end.toLocaleString() : 'N/A';
  const month = month.end ? month.end.toLocaleString() : 'N/A';

  return (
    <div className={`modal fade ${isOpen ? 'show' : ''}`} tabIndex="-1" role="dialog" style={{ display: isOpen ? 'block' : 'none' }}>
      <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">{event.title} {event.month}</h5>
            <button type="button" className="close" onClick={onClose}>
              <span>&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <p>Start: {start}</p>
            <p>End: {end}</p>
            <p>Description: {event.description}</p>
            <p>month: {month}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
