import React, { useState } from 'react';
import axios from 'axios';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import CustomModal from './Modal'; // Import the Modal component

const localizer = momentLocalizer(moment);

function App() {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleEventClick = event => {
    setSelectedEvent(event);
  };

  const closeModal = () => {
    setSelectedEvent(null);
  };

  return (
    <div className="App">
      <h1>Calendar Events</h1>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        onSelectEvent={handleEventClick} // Add this prop to handle event click
      />
      <CustomModal
        show={selectedEvent !== null}
        handleClose={closeModal}
        title="Event Details"
        content={
          selectedEvent && (
            <div>
              <h2>{selectedEvent.title}</h2>
              <p>Start: {moment(selectedEvent.start).format('LLLL')}</p>
              <p>End: {moment(selectedEvent.end).format('LLLL')}</p>
            </div>
          )
        }
      />
    </div>
  );
}

export default App;
