import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import Modal from 'react-modal';
import axios from 'axios';
import "./../node_modules/bootstrap/dist/css/bootstrap.min.css"
import './styles.css'; // Import your CSS file

const CalendarComponent = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isModalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleDateClick = (info) => {
    setModalOpen(true);
  };

  const handleEventClick = (info) => {
    setSelectedEvent(info.event);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setSelectedEvent(null);
  };

  return (
    <div>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        dateClick={handleDateClick}
        eventClick={handleEventClick}
      />
      <Modal  isOpen={isModalOpen} onRequestClose={closeModal} ariaHideApp={false} className="modal-container">
        {selectedEvent && (
          <div>
            <h2>{selectedEvent.title}</h2>
            <p>Date: {selectedEvent.start.toLocaleDateString()}</p>
            <p>Time: {selectedEvent.start.toLocaleTimeString()}</p>
            <button onClick={closeModal}>Close</button>
          </div>
        )}
      </Modal>
      <button className='btn btn-danger'>hlo</button>
    </div>
   
  );
};

export default CalendarComponent;
